mapper.select:
select empno, ename, job, mgr, hiredate, sal, comm, deptno from ( select rownum rn, empno, ename, job, mgr, hiredate, sal, comm, deptno from emp where status = 'y' order by
empNo ename job mgr hireDate sal comm deptNo
asc desc
) where (#{cri.page} - 1) * #{cri.amount} < rn AND rn <= #{cri.page} * #{cri.amount}

mapper.update:
update emp
set ename = #{ename}, job = #{job}, mgr = #{mgr}, sal = #{sal}
	, comm = #{comm}, deptNo = #{deptNo}
where empno = #{empNo}

update emp
set status = 'n'
where empno = #{empNo}
